package LOOPS;
import java.util.Scanner;
public class Power {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int a = sc.nextInt();
		int b = sc.nextInt();
		int c =0;
		
		for(int i=0 ; i<b; i++)
		{
			 c = a * a ;
		
		
	}
		System.out.println(c);
	}
	
}
